ue="<?= $info->slug; ?>" class="form-control" id="input_slug" readonly>						<span id="" class="error mt-2 text-danger" for=""><?php echo form_error('slug'); ?></span>					</div>						<button type="submit" class="btn btn-primary mr-2"><i class="ti-save"></i> Lưu lại</button>					<a href="<?= admin_url('city') ?>" class="btn btn-light"><i class="ti-arrow-left"></i> Quay lại danh sách</a>				</div>			</div>		</div>		<div class="col-md-4 grid-margin stretch-card">			<div class="card">				<div class="card-body">					<h4 class="card-title">Tùy chọn khác</h4>										<div class="form-group">						<label for="exampleInputEmail1">Thứ tự</label>						<input type="number" name="is_order" value="<?= $info->is_order; ?>" class="form-control" id="" placeholder="Thứ tự hiển thị">					</div>					<div class="form-group">						<label for="exampleFormControlSelect3">Hiển thị</label>						<select name="display" class="form-control form-control-sm" id="">							<option value="0">--Không chọn--</option>							<option value="1" <?= ($info->display) == 1 ? 'selected' : '' ?>>Trang chủ</option>							<option value="2" <?= ($info->display) == 2 ? 'selected' : '' ?>>Chân trang</option>						</select>					</div>					<div class="form-group">						<label for="exampleFormControlSelect3">Trạng thái</label>						<select name="status" class="form-control form-control-sm" id="exampleFormControlSelect4">							<?php echo yes_no($info->status); ?>						</select>					</div>					<div class="form-group">						<label>Hình ảnh</label>						<div class="input-group col-xs-12">							<input type="text" name="image" id="xFilePath" class="form-control file-upload-info" placeholder="Upload Image" value="<?= $info->image; ?>">							<span class="input-group-append">								<button class="file-upload-browse btn btn-primary" id="" onclick="browseServer()" type="button">Chọn ảnh</button>							</span>						</div>						<div class="col-xs-12">							<img src="<?= $info->image